import React from 'react';
import { Button } from 'reactstrap';
import './LearningListItem.scss';
import {withRouter,Link} from 'react-router-dom';

const LearningListItem=({item})=>{
    const {imageUrl,title,summary,id}=item;
    return (
        <div className="lp-list-item ">
            <div className="lp-img-container">
                <img  className="lp-img" src={imageUrl} />
            </div>
            <div className="lp-item-content ">
                <div className="lp-item-title">
                    {title}
                </div>

                <div className="lp-item-txt">
                     {summary}
                </div>
                <div className="lp-item-btn-container">
                    <Link to={{pathname: `/Learning/${title}`,state:{item}}}>
                    <button className="lp-item-btn" 
                    // onClick={()=>history.push(`/Learning/${title}`)}
                    >Read More..</button>
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default withRouter(LearningListItem);
