import React from 'react';
import {withRouter} from 'react-router-dom';
import  './LearningItem.styles.scss';

function LearningItem({location}) {
    const prop=location.state.item;
    const {title,imageUrl,text}=prop;


    return (
        <div className="lp-item-view">
            <div>
                <h1>
                    {title}
                </h1>
            </div>
            <div>
                <img src={imageUrl} alt="image"/>
            </div>
            <div>
                    {text}
                    {text}
                    {text}
            </div>
        </div>
    );
}

export default withRouter(LearningItem);
